import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import UsersTopBar from "../Users/TopBar/UsersTopBar.js";

import styled from "styled-components";

function SWProfile(props) {
  const [worker, setWorker] = useState({});

  useEffect(() => {
    const singleWorker = props.serviceWorkers.filter(sw => {
      return sw.id === parseInt(props.match.params.id);
    });
    setWorker(...singleWorker);
  }, []);

  return (
    <Wrapper>
      <UsersTopBar fullName={props.fullName} />
      <Hero>
        <div>
          <img src={worker.photoUrl} alt={worker.fullName} />
        </div>
        <p>{worker.fullName}</p>
      </Hero>
    </Wrapper>
  );
}

const mapStateToProps = state => {
  return {
    fullName: state.fullName,
    serviceWorkers: state.serviceWorkers
  };
};

export default connect(
  mapStateToProps,
  {}
)(SWProfile);

const Wrapper = styled.div`
  width: 100vw;
  height: 100vh;
  background: rgba(0, 0, 0, 0.1);
`;

const Hero = styled.div`
  width: 90%;
  max-width: 1100px;
  min-height: 400px;
  background: white;
  border-radius: 5px;
  box-shadow: 0 5px 10px rbga(0, 0, 0, 0.04);
  margin: 40px auto;
`;
